package week1.day1;

public class CheckIfTheNumberIsPositiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numCheck = -5;
		if(numCheck>0) {
			System.out.println("The given number is positive! "+numCheck);
		}else {
			System.out.println("The given number is negative! "+numCheck);
		}
			
	}

}
